mapper.select:
SELECT COUNT(DISTINCT a.id)
FROM t_article a
LEFT JOIN t_article_tag tat ON a.id = tat.article_id

    is_delete = #{param.isDelete}

        and article_title like concat('%',#{param.keyword},'%')


        and `status` = #{param.status}


        and article_type = #{param.articleType}


        and category_id = #{param.categoryId}


        and tat.tag_id = #{param.tagId}

SELECT a.id, article_cover, article_title, article_type, category_name, is_top, is_recommend, a.is_delete, a.`status`, a.create_time, t.id AS tag_id, t.tag_name FROM ( SELECT id, article_cover, category_id, article_title, article_type, is_top, is_recommend, is_delete, `status`, create_time FROM t_article
is_delete = #{param.isDelete}
and article_title like concat('%',#{param.keyword},'%')
and status = #{param.status}
and category_id = #{param.categoryId}
and article_type = #{param.articleType}
and id in ( SELECT article_id FROM t_article_tag WHERE tag_id = #{param.tagId} )
ORDER BY is_top DESC, is_recommend DESC, id DESC LIMIT #{param.current}, #{param.size} ) a LEFT JOIN t_category c ON a.category_id = c.id LEFT JOIN t_article_tag tat ON a.id = tat.article_id LEFT JOIN t_tag t ON t.id = tat.tag_id

SELECT a.id, article_cover, article_title, a.create_time, c.id AS category_id, c.category_name, t.id AS tag_id, tag_name FROM t_article a INNER JOIN t_category c ON a.category_id = c.id INNER JOIN t_article_tag tat ON a.id = tat.article_id INNER JOIN t_tag t ON tat.tag_id = t.id
is_delete = 0 and `status` = 1
and category_id = #{param.categoryId}
and tag_id = #{param.tagId}
ORDER BY a.id DESC LIMIT #{param.current}, #{param.size}
