mapper.select:
SELECT count(c.id) from t_comment c LEFT JOIN t_user u ON c.from_uid = u.id
c.comment_type=#{param.commentType}
AND u.nickname LIKE concat('%',#{param.keyword},'%')
AND c.is_check = #{param.isCheck}

SELECT c.id, u.avatar, u.nickname AS from_nickname, r.nickname AS to_nickname, a.article_title, c.comment_content, c.comment_type, c.is_check, c.create_time FROM t_comment c LEFT JOIN t_article a ON c.type_id = a.id LEFT JOIN t_user u ON c.from_uid = u.id LEFT JOIN t_user r ON c.to_uid = r.id
c.comment_type=#{param.commentType}
AND u.nickname LIKE concat('%',#{param.keyword},'%')
AND c.is_check = #{param.isCheck}
ORDER BY c.id DESC LIMIT #{param.current},#{param.size}

SELECT id
FROM t_comment
WHERE parent_id = #{parentId}

SELECT type_id as id, COUNT(*) AS comment_count FROM t_comment WHERE comment_type=#{commentType} AND type_id IN
#{typeId}
GROUP BY type_id

SELECT c.id, c.from_uid, u.nickname AS fromNickname,u.web_site, u.avatar, c.comment_content, c.create_time
FROM t_comment c
INNER JOIN t_user u ON c.from_uid = u.id


        type_id=#{param.typeId}

    AND comment_type=#{param.commentType}
    AND c.is_check=1
    AND parent_id IS NULL

ORDER BY c.id DESC
LIMIT #{param.current},#{param.size}

SELECT * FROM ( SELECT c.from_uid, u.nickname AS from_nickname, u.avatar, c.to_uid, r.nickname AS to_nickname, c.id, c.parent_id, c.comment_content, c.create_time, ROW_NUMBER() over (PARTITION BY parent_id ORDER BY create_time ASC ) row_num FROM t_comment c JOIN t_user u ON c.from_uid = u.id JOIN t_user r ON c.to_uid = r.id WHERE c.is_check = 1 AND parent_id IN
#{commentId}
) t WHERE 4 > row_num

SELECT parent_id as comment_id, count(1) AS reply_count FROM t_comment WHERE is_check = 1 AND parent_id IN
#{commentId}
GROUP BY parent_id
